class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        int N = queries.size();
        vector<bool> ans;
        int M = min(50000, N * 3);

        segtree<int, op, e> seg(M + 10);
        set<int> s = {0, M + 1};
        REP(i, N) {
            int op = queries[i][0];
            if (op == 1) {
                int x = queries[i][1];
                auto it = s.lower_bound(x);
                int l = *prev(it), r = *it;
                s.insert(x);
                seg.set(r, r - x);
                seg.set(x, x - l);
            } else {
                int x = queries[i][1], sz = queries[i][2];
                int mx = seg.prod(0, x + 1);
                auto it = s.upper_bound(x);
                chmax(mx, x - *prev(it));
                ans.pb(mx >= sz);
            }
        }
        return ans;
    }
};
