class Solution {
public:


static const int MAXN=5e4+5;

int t[4*MAXN];
int INF=1e9;
//use 1-based indexing
void build(int a[], int v, int tl, int tr) {
    if (tl == tr) {
        t[v] = a[tl];
    } else {
        int tm = (tl + tr) / 2;
        build(a, v*2, tl, tm);
        build(a, v*2+1, tm+1, tr);
        t[v] = max(t[v*2],t[v*2+1]);//change
    }
}

int sum(int v, int tl, int tr, int l, int r) {
    if (l > r) 
        return 0;
    if (l == tl && r == tr) {
        return t[v];
    }
    int tm = (tl + tr) / 2;
    return max(sum(v*2, tl, tm, l, min(r, tm))
           ,sum(v*2+1, tm+1, tr, max(l, tm+1), r)); //change
}

void update(int v, int tl, int tr, int pos, int new_val) {
    if (tl == tr) {
        t[v] = new_val;
    } else {
        int tm = (tl + tr) / 2;
        if (pos <= tm)
            update(v*2, tl, tm, pos, new_val);
        else
            update(v*2+1, tm+1, tr, pos, new_val);
        t[v] = max(t[v*2] ,t[v*2+1]); //change
    }
}
    vector<bool> getResults(vector<vector<int>>& queries) {
        int a[MAXN];
        for(int i=0;i<MAXN;i++) a[i]=-INF;
        build(a,1,1,MAXN-1);
        set<int> s;
        vector<bool> ans;
        for(auto v:queries)
        {
            if(v[0]==1)
            {
                auto it=s.lower_bound(v[1]);
                if(it!=s.end())
                {
                a[*it]=*it-v[1];
                update(1,1,MAXN-1,*it,a[*it]);
                }
                if(it==s.begin()) a[v[1]]=v[1];
                else{
                    it--;
                    a[v[1]]=v[1]-*it;
                }
                update(1,1,MAXN-1,v[1],a[v[1]]);
                s.insert(v[1]);
            }
            else{
                int curr=v[1];

                auto it=s.upper_bound(v[1]);
                if(it!=s.begin())
                {   it--;
                    curr=v[1]-*it;
                }
                if(max(curr,sum(1,1,MAXN-1,1,v[1]))>=v[2])
                {
                    ans.push_back(true);
                 }
                else ans.push_back(false);
            }
        }
        return ans;

    }
};
