class Solution {
public:
    int seg[600005];
    void build(int ind,int st,int se,int pos,int val) {
        if(st == se) {
            seg[ind] = val;
            return;
        }
        int mid = (st+se)>>1;
        if(pos <= mid) build(2*ind,st,mid,pos,val);
        else build(2*ind+1,mid+1,se,pos,val);
        seg[ind] = max(seg[2*ind],seg[2*ind+1]);
    }

    int query(int ind,int st,int se,int l,int r) {
        if(st > r or se < l) return 0;
        if(st >= l and se <= r) return seg[ind];
        int mid = (st+se)>>1;
        return max(query(2*ind,st,mid,l,r),query(2*ind+1,mid+1,se,l,r));
    }

    vector<bool> getResults(vector<vector<int>>& queries) {
        set<int> s;
        s.insert(0);
        s.insert(200000);
        int p = 0,q = 100000;
        vector<bool> ans;
        for(int i = 0; i < queries.size(); i++) {
            int e = queries[i][0];
            if(e == 1) {
                int se = queries[i][1];
                auto it = s.lower_bound(se);
                int dif = *it - se;
                int c = *it;
                it--;
                int dif1 = se-*it;
                build(1,p,q,se,dif1);
                build(1,p,q,c,dif);
                s.insert(se);
            } else {
                int x = queries[i][1],sz = queries[i][2];
                auto it = s.lower_bound(x);
                if(*it > x)
                    it--;
                int e = x-*it;
                int mx = query(1,p,q,0,*it);
                //cout << *it << ' ' << mx << ' ' << e << endl;
                mx = max(mx,e);
                if(mx >= sz) ans.push_back(true);
                else ans.push_back(false);
            }
        }
        return ans;
    }
};
