struct tree {
    int st[50010 * 4] = {0};
    int qry(int i, int j, int p = 1, int l = 0, int r = 50004){
        if (r < i || j < l) return 0;
        if (i <= l && r <= j) return st[p];
        int m = (l + r) / 2;
        return max(qry(i, j, p * 2, l, m), qry(i, j, p * 2 + 1, m + 1, r));
    }
    void sets(int i, int x, int p = 1, int l = 0, int r = 50004){
        if(l == r){
            st[p] = x;
        }else{
            int m = (l + r) / 2;
            if (i <= m) sets(i, x, p * 2, l, m);
            else sets(i, x, p * 2 + 1, m + 1, r);
            st[p] = max(st[p * 2], st[p * 2 + 1]);
        }
    }
};

class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        tree str;
        set<int> st;
        st.insert(0);
        st.insert(50005);
        vector<bool> ans;
        str.sets(0, 50005);
        for(auto v: queries){
            if(v[0] == 1){
                auto it = prev(st.lower_bound(v[1]));
                str.sets(*it, v[1] - *it);
                it = st.lower_bound(v[1]);
                str.sets(v[1], *it - v[1]);
                st.insert(v[1]);
            }else{
                v[1] -= v[2];
                if(v[1] < 0) ans.push_back(false);
                else{
                    ans.push_back(str.qry(0, v[1]) >= v[2]);
                }
            }
        }
        return ans;
    }
};
