class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        w.insert(0);
        w.insert(INT_MAX/2);
        m[INT_MAX/2].insert(0);
        for(auto e : queries)
        {
            if(e[0]==1) insertWall(e[1]);
            else r.push_back(checkSize(e[1],e[2]));
            //for(auto e : w) cout<<e<<" ";
            //cout<<endl;
            //for(auto [k,v] : m)
            //{
            //    cout<<"WS: "<<k<<" : ";
            //    for(auto e : v) cout<<e<<" ";
            //    cout<<endl;
            //}
            //cout<<endl;
        }
        return r;
    }
private:
    void insertWall(int wallP)
    {
        w.insert(wallP);
        auto it = w.find(wallP);
        int pw = *prev(it);
        if(next(it)!=w.end())
        {
            cout<<"SPlit"<<endl;
            int nw = *next(it);
            m[nw-pw].erase(pw);
            if(m[nw-pw].empty()) m.erase(nw-pw);
            m[nw-wallP].insert(wallP);
        }
        m[wallP-pw].insert(pw);
    }
    bool checkSize(int end, int sz)
    {
        for(auto it = m.lower_bound(sz);it!=m.end();it++)
        {
            int s = *it->second.begin();
            if(s+sz<=end) return true;
        }
        return false;
    }
    map<int,set<int>> m;
    set<int> w;
    vector<bool> r;
};
