class Solution {
public:

    int dat[400055];

    void build(int id, int x, int y) {
        if (x == y) {
            dat[id] = 0;
            return;
        }

        int mid = (x + y) >> 1;

        build(id + id, x, mid);
        build(id + id + 1, mid + 1, y);

        dat[id] = 0;
    }
    void update(int id, int x, int y, int pos, int key) {
        if (x == y) {
            dat[id] = key;
            return;
        }

        int mid = (x + y) >> 1;

        if (pos <= mid) update(id + id, x, mid, pos, key);
        else update(id + id + 1, mid + 1, y, pos, key);

        dat[id] = max(dat[id + id], dat[id + id + 1]);
    }
    int query(int id, int x, int y, int l, int r) {
        if (l > y || r < x) return 0;

        if (l <= x && y <= r) {
            return dat[id];
        }

        int mid = (x + y) >> 1;

        return max(query(id + id, x, mid, l, r), query(id + id + 1, mid + 1, y, l, r));
    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> res;

        int m = (int)queries.size();

        set<pair<int, int>> s;

        s.emplace(0, (int)1e5);

        build(1, 0, (int)1e5);
        update(1, 0, (int)1e5, 0, (int)1e5);

        for (int i = 0; i < m; i++) {
            int ope = queries[i][0];

            if (ope == 1) {
                int x = queries[i][1];
                auto ite = --s.upper_bound({x + 1, -1});

                auto p = *ite;

                s.erase(ite);

                s.emplace(p.first, x);
                s.emplace(x, p.second);

                update(1, 0, (int)1e5, p.first, x - p.first);
                update(1, 0, (int)1e5, x, p.second - x);
            } else {
                int x = queries[i][1];
                int sz = queries[i][2];

                res.push_back(query(1, 0, (int)1e5, 0, x - sz) >= sz);
            }
        }

        return res;
    }
};
