#define pb push_back
#define ff first
#define ss second
#define all(x) (x).begin(),(x).end()
typedef long long int ll;
typedef unsigned long long int ull;
typedef long double ld;

void update(vector<int> &tree, int node, int start, int end, int idx, int val)
{
    if(start == end)
    {
        tree[node] = val;
    }
    else
    {
        int mid = (start + end) / 2;
        if(start <= idx and idx <= mid)
        {
            update(tree, 2*node, start, mid, idx, val);
        }
        else
        {
            update(tree, 2*node+1, mid+1, end, idx, val);
        }
        tree[node] = max(tree[2*node], tree[2*node+1]);
    }
}

int query(vector<int> &tree, int node, int start, int end, int l, int r)
{
    if(r < start or end < l)
    {
        return 0;
    }
    if(l <= start and end <= r)
    {
        return tree[node];
    }
    int mid = (start + end) / 2;
    int p1 = query(tree, 2*node, start, mid, l, r);
    int p2 = query(tree, 2*node+1, mid+1, end, l, r);
    return max(p1, p2);
}

class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& ques)
    {
        int n = 3e5;
        vector<bool> ans;
        set<int> st; st.insert(0);
        vector<int> tree(4*n, 0);
        for(vector<int> q: ques)
        {
            int t = q[0];
            if(t==1)
            {
                int x = q[1];
                auto it = st.upper_bound(x);
                if(it!=st.end())
                {
                    int dr = (*it)-x;
                    update(tree, 1, 0, n-1, (*it), dr);
                }
                it--;
                int dl = x-(*it);
                update(tree, 1, 0, n-1, x, dl);
                st.insert(x);
            }
            else
            {
                int x = q[1], z = q[2];
                auto it = st.upper_bound(x);
                it--;
                int val = x-(*it);
                int nval = query(tree, 1, 0, n-1, 0, x);
                val = max(val, nval);
                if(val >= z) ans.pb(true);
                else ans.pb(false);
            }
        }
        return ans;
    }
};
