class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& q) {
        bitset <50002> b;
        vector <bool> ans;
        b[0] = 1;
        for (auto it : q) {
            int x = it[1];
            if (it[0] == 1) {
                b[x] = 1;
            }
            else {
                int sz = it[2];
                bool f = 0;
                int j = 0;
                for (int i = b._Find_next(sz - 1); i <= x; i = b._Find_next(i + sz - 1)) {
                    if (i >= sz && b._Find_next(i - sz) >= i) {
                        f = 1;
                        // cout << i << ' ' << b._Find_next(i - sz) << endl;
                        break;
                    }
                    j = i;
                }
                if (x >=sz && !b[x] && b._Find_next(x - sz) >= x) {
                    f = 1;
                }
                // if (x - j >= sz) f = 1;
                ans.push_back(f);
            }
        }
        return ans;
    }
};

