class Solution {
public:
    #define ll int
    ll seg[400001];
    ll query(ll ind,ll low,ll high,ll l ,ll r)
    {
        if(low>=l&&high<=r)
        {
            return seg[ind];
        }
        if(high<l||low>r)
        {
            return 0;
        }
        ll mid=(low+high)/2;
        ll left=query(ind*2,low,mid,l,r);
        ll right=query(ind*2+1,mid+1,high,l,r);
        return max(left,right);
    }
    void update(ll ind,ll low,ll high,ll node,ll val)
    {
        if(low==high)
        {
            seg[ind]=val;
        }
        else
        {
            ll mid=(low+high)/2;
            if(low<=node&&mid>=node)
            {
                update(ind*2,low,mid,node,val);
            }
            else
            update(ind*2+1,mid+1,high,node,val);
            seg[ind]=max(seg[ind*2],seg[ind*2+1]);
        }
    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        set<int>s;
        int n=1e5;
        s.insert(1e5);
        vector<bool>ans;
        for(auto a:queries)
        {
            if(a.size()==2)
            {
                auto it=s.upper_bound(a[1]);
                update(1,1,n,a[1],*it-a[1]);
                if(it!=s.begin())
                {
                    it--;
                    update(1,1,n,*it,a[1]-*it);
                }
                s.insert(a[1]);
            }
            else
            {
                if(a[2]>a[1])
                {
                    ans.push_back(false);
                    continue;
                }
                if(*s.begin()>=a[2]||query(1,1,n,1,a[1]-a[2])>=a[2])
                {
                    ans.push_back(true);
                    continue;
                }
                ans.push_back(false);
            }
        }
        return ans;
    }
};
