class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        int blk=200,len=260; int a[305]={0},b[305]={0},info[305]={0};
        int la[305],ra[305]={0};
        for(int i=1;i<=len;++i) la[i]=500000,a[i]=blk*i,b[i]=blk*i;
        bool fl[50005]={0}; fl[0]=1;
        vector<bool> ans;
        for(auto o:queries){
            //add
            if (o.size()==2){
                int x=o[1];
                fl[x]=1; int bel=(x+blk-1)/blk;
                //change bel;
                la[bel]=min(la[bel],x); ra[bel]=max(ra[bel],x);
                int ma=a[bel-1],now=b[bel-1];
                for(int i=(bel-1)*blk+1;i<=bel*blk;++i){
                    ++now,ma=max(ma,now);
                    if (fl[i]==1) now=0;
                }
                a[bel]=max(ma,a[bel-1]),b[bel]=now;
                //
                //cout<<"!"<<bel<<ma<<now<<endl;
                //change info
                if (la[bel]<ra[bel]){
                    int qw=0,er=0;
                    for(int i=la[bel]+1;i<=ra[bel];++i){
                        ++er,qw=max(qw,er);
                        if (fl[i]==1) er=0;
                    }
                    info[bel]=qw;
                }
                //change behind
                for(int i=bel+1;i<=len;++i){
                    if (!ra[i]){
                        now+=blk; ma=max(now,ma);
                        a[i]=max(ma,a[i-1]); b[i]=now;
                    }
                    else{
                        int l=(i-1)*blk,r=i*blk;
                        a[i]=max(a[i-1],max(now+la[i]-l,max(info[i],r-ra[i])));
                        now=b[i]=r-ra[i];
                    }
                    //cout<<i<<a[i]<<b[i]<<endl;
                }
            }
            //query
            else{
                int x=o[1],y=o[2]; int bel=(x+blk-1)/blk;
                int ma=a[bel-1],now=b[bel-1];
                for(int i=(bel-1)*blk+1;i<=x;++i){
                    ++now,ma=max(ma,now);
                    if (fl[i]==1) now=0;
                }
                printf("#%d\n",ma);
                if (ma>=y) ans.push_back(true); else ans.push_back(false);
            }
        }
        return ans;
    }
};
