class Node:
    def __init__(self, lo, hi, left, right, maxw, wl, wr):
        self.lo=lo
        self.hi=hi
        self.left=left
        self.right=right
        self.maxw=maxw
        self.wl=wl
        self.wr=wr
        self.isobstacle=False
    def query1(self, x):
        if self.lo==self.hi: 
            self.isobstacle=True
            return
        self.isobstacle=True
        if self.left.hi >= x: self.left.query1(x)
        else: self.right.query1(x)
        self.maxw=max(self.left.maxw, self.left.wr+1+self.right.wl, self.right.maxw)
        if self.left.isobstacle:
            self.wl = self.left.wl
        else:
            self.wl = self.left.maxw + 1 + self.right.wl
        if self.right.isobstacle:
            self.wr = self.right.wr
        else: 
            self.wr = self.left.wr + 1 + self.right.maxw
    def query2(self, x, sz):
        # print("query2",self.lo, self.hi, x, sz)
        if self.hi <= x: return self.maxw >= sz
        if self.lo==self.hi: return False
        if self.left.hi >= x: return self.left.query2(x, sz)
        if self.left.maxw>=sz or self.right.query2(x,sz): return True
        midw = self.left.wr + 1 + min(self.right.lo + self.right.wl, x)-self.right.lo
        return midw >= sz

def createNode(n):
    def create(lo,hi):
        if lo==hi:
            return Node(lo,hi,None,None,0,0,0)
        mid=(lo+hi)//2
        return Node(lo,hi,create(lo,mid),create(mid+1,hi),hi-lo,hi-lo,hi-lo)
    return create(0, n)    

class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        node = createNode(max(q[1] for q in queries))
        res=[]
        for q in queries:
            if q[0]==1:
                node.query1(q[1])
                continue
            if q[2]>q[1]: res.append(False)
            elif q[2]==1: res.append(True)
            else: res.append(node.query2(q[1], q[2]))
        return res
