class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& a) {
        int n=min((int)a.size()*3,50000);
        vector<int> f(4*n+10,0);
        vector<int> lazy(4*n+10,0);
        function<void(int,int,int)> push_down=[&](int cur,int lc,int rc){
            int tmp=lazy[cur];
            if(tmp==0)
                return;
            f[lc]-=tmp;
            f[rc]-=tmp;
            lazy[lc]+=tmp;
            lazy[rc]+=tmp;
            lazy[cur]=0;
        };
        function<void(int,int,int,int,int,int)> modify=[&](int cur,int l,int r,int x,int y,int k){
            if(x==l&&y==r){
                f[cur]-=k;
                lazy[cur]+=k;
                return;
            }
            int t=cur<<1;
            int mid=l+r>>1;
            push_down(cur,t,t+1);
            if(x<=mid)
                modify(t,l,mid,x,min(mid,y),k);
            if(mid<y)
                modify(t+1,mid+1,r,max(mid+1,x),y,k);
            f[cur]=max(f[t],f[t+1]);
        };
        function<int(int,int,int,int,int)> query=[&](int cur,int l,int r,int x,int y){
            if(x==l&&y==r)
                return f[cur];
            int t=cur<<1;
            int mid=l+r>>1;
            push_down(cur,t,t+1);
            int retl=0;
            int retr=0;
            if(x<=mid)
                retl=query(t,l,mid,x,min(mid,y));
            if(mid<y)
                retr=query(t+1,mid+1,r,max(mid+1,x),y);
            return max(retl,retr);
        };
        function<void(int,int,int)> build=[&](int cur,int l,int r){
            if(l==r){
                f[cur]=l;
                return;
            }
            int t=cur<<1;
            int mid=l+r>>1;
            build(t,l,mid);
            build(t+1,mid+1,r);
            f[cur]=max(f[t],f[t+1]);
        };
        build(1,1,n+1);
        set<int> all;
        all.insert(0);
        all.insert(n+1);
        vector<bool> ans;
        for(auto &v:a){
            auto tp=v[0];
            auto x=v[1];
            if(tp==1){
                auto it=all.upper_bound(x);
                int nxt=*it;
                int pre=*(--it);
                modify(1,1,n+1,x+1,nxt,x-pre);
                all.insert(x);
            }else{
                auto y=v[2];
                if(query(1,1,n+1,1,x)>=y)
                    ans.push_back(true);
                else
                    ans.push_back(false);
            }
        }
        return ans;
    }
};
