class Solution {
public:
    int INF = 1e5;
    int maxv[4 * 500010], tag[4 * 500010];
    void build(int u, int l, int r) {
        tag[u] = 0;
        if (l == r) {
            maxv[u] = l;
            return;
        }
        int mid = (l + r) >> 1;
        build(u << 1, l, mid);
        build(u << 1 | 1, mid + 1, r);
        maxv[u] = std::max(maxv[u << 1], maxv[u << 1 | 1]);
    }
    void push(int u) {
        if (tag[u]) {
            maxv[u << 1] += tag[u];
            maxv[u << 1 | 1] += tag[u];
            tag[u << 1] += tag[u];
            tag[u << 1 | 1] += tag[u];
            tag[u] = 0;
        }
    }
    void modify(int u, int l, int r, int ql, int qr, int v) {
        if (l >= ql && r <= qr) {
            maxv[u] += v;
            tag[u] += v;
            return;
        }
        int mid = (l + r) >> 1;
        push(u);
        if (ql <= mid) modify(u << 1, l, mid, ql, qr, v);
        if (qr > mid) modify(u << 1 | 1, mid + 1, r, ql, qr, v);
        maxv[u] = std::max(maxv[u << 1], maxv[u << 1 | 1]);
    }
    int query(int u, int l, int r, int ql, int qr) {
        if (l >= ql && r <= qr) return maxv[u];
        push(u);
        int res = 0, mid = (l + r) >> 1;
        if (ql <= mid) res = std::max(res, query(u << 1, l, mid, ql, qr));
        if (qr > mid) res = std::max(res, query(u << 1 | 1, mid + 1, r, ql, qr));
        return res;
    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        std::set<int> se;
        se.insert(0), se.insert(INF);
        build(1, 0, INF);
        std::vector<bool> ans;
        for (auto &it : queries) {
            int op = it[0];
            if (op == 1) {
                int x = it[1];
                auto suf = se.upper_bound(x);
                auto pre = prev(suf);
                modify(1, 0, INF, x + 1, *suf, -(x - *pre));
                se.insert(x); 
            } else {
                int x = it[1], sz = it[2];
                ans.push_back(query(1, 0, INF, 0, x) >= sz);
            }
        }
        return ans;
    }
};
