class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        blocks = [0]
        max_dis = [0]
        ans = []
        for q in queries:
            if q[0] == 1:
                i = bisect.bisect(blocks, q[1])
                if i == len(blocks):
                    max_dis.append(max(max_dis[-1], q[1] - blocks[-1]))
                    blocks.append(q[1])
                else:
                    blocks.insert(i, q[1])
                    cur_max = max(max_dis[i-1], q[1] - blocks[i-1])
                    max_dis.insert(i, cur_max)
                    cur = i+1
                    while cur < len(max_dis) and max_dis[cur] > cur_max:
                        cur_max = max(cur_max, blocks[cur] - blocks[cur-1])
                        max_dis[cur] = cur_max
                        cur += 1
            else:
                i = bisect.bisect(blocks, q[1])
                ans.append(max(max_dis[i-1], q[1] - blocks[i-1]) >= q[2])
        return ans
