class Solution {
public:
    int b[100002<<2] = {}, lz[100002<<2] = {};
    void build(int l,int r,int idx){
        if(l==r){
            b[idx] = l;
            return;
        }
        int m = (l+r)>>1;
        build(l,m,idx<<1);
        build(m+1,r,idx<<1|1);
        b[idx] = max(b[idx<<1],b[idx<<1|1]);
    }
    void pushdown(int idx){
        if(lz[idx]){
            b[idx<<1] -= lz[idx];
            b[idx<<1|1] -= lz[idx];
            lz[idx<<1] += lz[idx];
            lz[idx<<1|1] += lz[idx];
            lz[idx] = 0;
        }
    }
    void modify(int l,int r,int idx,int q,int val){
        if(l==r){
            b[idx] = val;
            return;
        }
        int m = (l+r)>>1;
        pushdown(idx);
        if(q<=m) modify(l,m,idx<<1,q,val);
        else modify(m+1,r,idx<<1|1,q,val);
        b[idx] = max(b[idx<<1],b[idx<<1|1]);
    }
    void update(int l,int r,int idx,int ql,int qr,int val){
        if(ql<=l && r<=qr){
            lz[idx] += val;
            b[idx] -= val;
            return;
        }
        int m = (l+r)>>1;
        pushdown(idx);
        if(ql<=m) update(l,m,idx<<1,ql,qr,val);
        if(m< qr) update(m+1,r,idx<<1|1,ql,qr,val);
        b[idx] = max(b[idx<<1],b[idx<<1|1]);
    }
    int query(int l,int r,int idx,int ql,int qr){
        if(ql<=l && r<=qr) return b[idx];
        int m = (l+r)>>1, res = 0;
        pushdown(idx);
        if(ql<=m) res = max(res, query(l,m,idx<<1,ql,qr));
        if(m< qr) res = max(res, query(m+1,r,idx<<1|1,ql,qr));
        return res;
    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        build(1,100000,1);
        set<int> s;
        s.insert(100001);
        vector<bool> ans;
        for(vector<int>& q:queries){
            if(q[0]==1){
                int tail = *s.lower_bound(q[1]), val = query(1,100000,1,q[1],q[1]);
                s.insert(q[1]);
                update(1,100000,1,q[1]+1,tail,val);
            }else{
                int res = query(1,100000,1,1,q[1]);
                if(res>=q[2]) ans.push_back(true);
                else ans.push_back(false);
            }
        }
        return ans;
    }
};
