class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        map<int, int> mp;
        mp[0] = INT_MAX;
        map<int, set<int>> sizM;
        sizM[INT_MAX].insert(0);
        vector<bool> ans;
        for(auto &c: queries) {
            int x = c[1];
            if(c[0] == 1) {
                auto pos = mp.upper_bound(x);
                --pos;
                int a = (*pos).first, b = (*pos).second;
                int sizeOld = b-a;
                if(sizM[sizeOld].size() == 1) {
                    sizM.erase(sizeOld);
                } else {
                    sizM[sizeOld].erase(a);
                }
                mp.erase(pos);
                mp[a] = x;
                mp[x] = b;

                sizM[x-a].insert(a);
                sizM[b-x].insert(x);
            } else {
                auto pos = sizM.lower_bound(c[2]);
                bool answer = false;
                while(pos!=sizM.end()) {
                    int sz = (*pos).first;
                    for(auto &it: (*pos).second) {

                        auto lb = mp.upper_bound(it);
                        lb--;
                        if(x-(*lb).first>=c[2]) {
                            answer = true;
                        }
                        break;
                    }
                    if(answer == true) {
                        break;
                    }
                    pos++;
                }
                ans.push_back(answer);
            }
        }
        return ans;
    }
};
