#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (long long)(x).size()
#define pii pair<long long,long long>

template <class T> class SegmentTree{
private: 
    const T Default = {0,0,0,0};
    vector<T> seg;
    long long len;
public:
    T f(T a,T b){ 
        T ret={0,0,0,0};
        ret[0ll]=max(a[0ll],max(b[0ll],a[2]+b[1]));
        ret[1]=max(a[1],b[1]+a[3]);
        ret[2]=max(b[2],a[2]+b[3]);
        ret[3]=(a[3]+b[3]);
        return ret;
    }
    SegmentTree(long long len) : len(len),seg(2*len,Default){}
    void set(long long idx,T val){
        idx+=len;
        seg[idx]=val;
        for(;idx>1;idx/=2){
            if(idx%2==0)
                seg[idx/2]=f(seg[idx],seg[idx^1]);
            else
                seg[idx/2]=f(seg[idx^1],seg[idx]);
        }
    }
    T range(long long l,long long r){
        T sum1={0,0,0,0},sum2={0,0,0,0};
        if(r<=l)return {0ll,0ll,0ll,0ll};
        for(l+=len,r+=len;r>l;l/=2,r/=2){
            if(l&1){
                sum1=f(sum1,seg[l++]);
            }
            if(r&1){
                sum2=f(seg[--r],sum2);
            }
        }
        return f(sum1,sum2);
    }
};

class Solution {
public:
    vector<bool> getResults(vector<vector<int>> queries) {
        long long n=min(50005ll,3*sz(queries)+5); 
        SegmentTree<array<long long,4>> seg(n);
        for(int i=1;i<n;i++){
            seg.set(i,{1,1,1,1});
        }
        vector<bool> ret;
        for(auto x:queries){
            if(sz(x)==2){
                seg.set(x[1],{0,0,0,-INT_MAX});
            }   
            else{
                long long ans=seg.range(0,x[1])[0];
                if(ans+1>=x[2])
                    ret.pb(1);
                else
                    ret.pb(0);
            }
        }     
        return ret;
    }
};
