class Solution {
public:
    #define ll long long
    static const int N = 1e5;

    int tree[4*N+100];
    void update(ll idx, ll val, ll i, ll st, ll ed) {
        if(st == ed) {
            tree[i] = val;
            return;
        }
        ll mid = (st+ed)/2;
        if(idx <= mid) {
            update(idx, val, i*2+1, st, mid);
        }else {
            update(idx, val, i*2+2, mid+1, ed);
        }
        tree[i] = max(tree[i*2+1], tree[i*2+2]);
    }

    ll query(ll l, ll r, ll i, ll st, ll ed) {
        if(ed < l || st > r) {
            return 0;
        }
        if(st >= l && ed <= r) {
            return tree[i];
        }
        ll mid = (st+ed)/2;
        return max(query(l, r, i*2+1, st, mid), query(l, r, i*2+2, mid+1, ed));
    }



    vector<bool> getResults(vector<vector<int>>& queries) {
        map<int, int> mpp;
        mpp[0] = 0;
        mpp[1e5] = 1e5;

        memset(tree, 0, sizeof(tree));

        update(N, N, 0, 0, N-1);
        vector<bool> ans;


        for(auto &vec:queries) {
            if(vec[0] == 1) {
                int co = vec[1];
                auto it = mpp.upper_bound(co);
                int gr_co = it->first;

                it--;
                int sm_co = it->first;

                mpp[co] = co - sm_co;
                mpp[gr_co] = gr_co - co;
                update(co, mpp[co], 0, 0, N-1);
                update(gr_co, mpp[gr_co], 0, 0, N-1);
            } else {
                int co = vec[1];
                int sz = vec[2];

                auto it = mpp.lower_bound(co);
                it--;

                int sm_co = it->first;

                if(co - sm_co >= sz) {
                    ans.push_back(true);
                    continue;
                }

                int maxi = query(0, sm_co, 0, 0, N-1);
                if(maxi >= sz) {
                    ans.push_back(true);
                }else {
                    ans.push_back(false);
                }
            }
        }
        return ans;
    }
};
