class Solution {
    public List<Boolean> getResults(int[][] queries) {
        List<Boolean> result = new ArrayList<>();
        TreeMap<Integer, Integer> map = new TreeMap<>();
        map.put(0,0);
        for (int[] query: queries) {
            int type = query[0];
            int xCoord = query[1];
            if (type == 1) {
                int prevCoord = map.floorKey(xCoord);
                Integer nextCoord = map.ceilingKey(xCoord);
                int prevMax = map.get(prevCoord);
                int newMax = Math.max(xCoord - prevCoord, prevMax);
                map.put(xCoord, newMax);
                //System.out.print(map);
                while (nextCoord != null && map.get(nextCoord) > newMax) {
                    if (nextCoord - xCoord >= map.get(nextCoord))
                        break;
                    newMax = Math.max(nextCoord - xCoord, newMax);
                    map.put(nextCoord, newMax);
                    xCoord = nextCoord;
                    nextCoord = map.ceilingKey(nextCoord + 1);
                }
                //System.out.println(map);
            }
            if (type == 2) {
                int size = query[2];
                int prevCoord = map.floorKey(xCoord);
                boolean doesFit = size <= map.get(prevCoord) || size <= xCoord - prevCoord;
                result.add(doesFit);
            }
        }
        return result;
    }
}
