class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:

        from sortedcontainers import SortedList

        res = []
        sl = SortedList()
        sl.add(0)

        mem = [[0, 0]]

        for q in queries:
            if q[0] == 1:
                sl.add(q[1])

        lens = len(sl)

        for i in range(1, lens):
            spc = sl[i] - sl[i-1]
            if spc > mem[-1][-1]:
                mem.append([sl[i], spc])


        for q in queries[::-1]:

            if q[0] == 1:

                x = q[1]
                ps = sl.index(x)

                if ps == len(sl) - 1:
                    sl.remove(x)
                    if mem[-1][0] == x:
                        mem.pop()
                    continue
                else:
                    prev = sl[ps-1]
                    nxt = sl[ps+1]
                    sl.remove(x)
                    nspc = nxt - prev

                    psm = bisect.bisect_left(mem, [nxt, nspc])

                    if nspc > mem[psm-1][1]:
                        bisect.insort_left(mem, [nxt, nspc])
                        psm += 1

                        while psm < len(mem) and mem[psm][1] < nspc:
                            mem.pop(psm)

            else:
                _, x, sz = q
                ps = sl.bisect_left(x)

                if sz > x:
                    res.append(False)
                    continue

                if x - sl[ps-1] >= sz:
                    res.append(True)
                    continue

                psm = bisect.bisect_left(mem, [x, -1])
                if mem[psm-1][1] >= sz:
                    res.append(True)
                else:
                    res.append(False)


        return res[::-1]
