class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& q) {
        int n=q.size();
        multiset<int> pos;
        vector<bool>ans;
        multiset<int> dist;
        multiset<int> tmp;
        tmp.insert(0);
        for(int i=0;i<n;i++){
            if(q[i][0]==1){
                int newel=q[i][1];
                int maxi=*tmp.rbegin();
                if(newel>=maxi){
                    dist.insert(newel-maxi);
                }
                else{
                    auto it=tmp.lower_bound(newel);
                    auto upper=it;
                    it--;
                    dist.erase(dist.find(*upper-*it));
                    dist.insert(*upper-newel);
                    dist.insert(newel-*it);
                }
                tmp.insert(q[i][1]);
                pos.insert(q[i][1]);
            }
            else{
                int x=q[i][1];
                int sz=q[i][2];
                if(pos.size()==0){
                    if(x>=sz) ans.push_back(true);
                    else ans.push_back(false);
                    continue;
                }
                int last=*pos.rbegin();
                if(x-last>=sz){
                    ans.push_back(true);
                    continue;
                }
                if(*dist.rbegin()<sz){
                    ans.push_back(false);
                    continue;
                }
                int prev=0;
                bool chk=0;
                for(auto it:pos){
                    if(x>=prev && x<=it){
                        if(x-prev>=sz){
                            ans.push_back(true);
                            chk=1;
                            break;
                        }
                        break;
                    }
                    else{
                        if(it-prev>=sz){
                            ans.push_back(true);
                            chk=1;
                            break;
                        }
                    }
                    prev=it;
                }
                if(!chk) ans.push_back(false);
            }
        }
        return ans;
    }
};
