from sortedcontainers import SortedList
class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        s = SortedList()
        d = defaultdict(int)
        s.add(0)
        ans = []
        for i, q in enumerate(queries):
            x = q[1]
            if q[0] == 1:
                idx = bisect_left(s, x)
                d[x] = max(d[s[idx-1]], x-s[idx-1]) 
                s.add(x)
                idx += 1
                while idx <= len(s) - 1:
                    zhiqian = d[s[idx]]
                    zhihou = max(d[s[idx-1]], s[idx]-s[idx-1])
                    if zhihou < zhiqian:
                        d[s[idx]] = zhihou
                    else:
                        break
                    idx += 1
            else:
                sz = q[2]
                idx = bisect_left(s, x)
                if d[s[idx-1]] >= sz or (x-s[idx-1]) >= sz:
                    ans.append(True)
                else:
                    ans.append(False)
        print(d)
        return ans
