class Solution {
int maxn = 5e4+123;
public:
    int t[(int)2e6], p[(int)2e6], xl, xr, it;
    void build(int v, int l, int r){
        t[v] = r - 1, p[v] = -1;
        if(l + 1 == r){
            return;
        }
        build(v * 2 + 1, l, (l + r) >> 1), build(v * 2 + 2, (l + r) >> 1, r);
    }
    void push(int v, int r, int rr){ 
        p[v * 2 + 1] = p[v * 2 + 2] = p[v];
        t[v * 2 + 1] = r - p[v], t[v * 2 + 2] = rr - p[v];
        p[v] = -1;
    }
    void upd(int v, int l, int r){
        if(xl <= l && r <= xr){
            p[v] = xl - 1;
            t[v] = r - xl;
            return;
        }
        if(xr <= l || r <= xl)return;
        if(p[v] != -1){
            push(v, (l + r) / 2 - 1, r - 1);
        }
        upd(v * 2 + 1, l, (l + r) >> 1), upd(v * 2 + 2, (l + r) >> 1, r);
        t[v] = max(t[v * 2 + 1], t[v * 2 + 2]);
    }
    int get(int v, int l, int r){
        if(xl <= l && r <= xr){
            return t[v];
        }
        if(xr <= l || r <= xl)return 0;
        if(p[v] != -1){
            push(v, (l + r) / 2 - 1, r - 1);
        }
        int mx = max(get(v * 2 + 1, l, (l + r) / 2), get(v * 2 + 2, (l + r) / 2, r));
        return mx;
    }
    vector<bool> getResults(vector<vector<int>>& q) {
        vector<bool> ret;
        set<int> hv;
        hv.insert(maxn - 1);
        build(0, 0, maxn);
        for(auto i : q){
            if(i[0] == 2){
                xl = 0, xr = i[1] + 1;
                if(get(0, 0, maxn) >= i[2])ret.push_back(1);
                else ret.push_back(0);
            }
            else {
                hv.insert(i[1]);
                xl = i[1] + 1, xr = *hv.upper_bound(i[1]) + 1;
                upd(0, 0, maxn);
            }
        }
        return ret;
    }
};
