#define deb2(x, y) cout<<#x<<"="<<x<<","<<#y<<"="<<y<<endl
class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& Q) {
        set<int> s;
        vector<bool> ans;
        unordered_map<int,int> m;
        for(auto& y:Q){
            int q=y[0];
            int x=y[1];
            if(q==1){
                s.insert(x);
                continue;
            }
            int sz=y[2];
            int xx=x;
            bool flag=true;
            while(1){
                int d=x-sz;
                if(m.find(x)!=m.end() and m[x]<=sz){
                    flag=false;
                    break;
                }
                if(d<0){
                    flag=false;
                    break;
                }
                auto it=s.upper_bound(d);
                if(it==s.end()) break;
                if(*it>=x) break;
                x=*it;
            }
            if(flag==false){
                if(m.find(xx)!=m.end()) m[xx]=min(m[xx],sz);
                else m[xx]=sz;
            }
            ans.push_back(flag);
        }
        return ans;
    }
};
