class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> res;
        map<int, int> mp;
        mp[0] = 1e7;
        // cout << "-----\n";
        map<int, set<int>> dis;
        dis[1e7].insert(0);
        for (auto& q : queries) {
            int type = q[0], x = q[1];
            if (type == 1) {
                // cout << "type:1\n";
                auto it = mp.lower_bound(q[1]);
                it--;
                int len = it->second, cur = it->first, nx = cur + len;
                // cout << len << ' ' << cur << ' ' << nx << ' ' << q[1] << '\n';
                it->second = x - cur;
                mp[x] = nx - x;
                dis[len].erase(cur);
                dis[x - cur].insert(cur);
                dis[nx - x].insert(x);
                if (dis[len].empty()) {
                    dis.erase(len);
                }
            } else {
                // cout << "type:2\n";
                int sz = q[2], prev = 0;
                bool can = false;
                auto it = dis.lower_bound(sz);
                while (it != dis.end()) {
                    auto f = it->second.begin();
                    if (*f <= x - sz) {
                        can = true;
                        break;
                    }
                    it++;
                }
                res.push_back(can);
            }
        }
        return res;
    }
};

