const int N=1e5+5;
class Solution {
public:

    int tre[4*N]={0},lazy[4*N]={0};
    void downlazy(int k){
        tre[k+k]+=lazy[k];
        tre[k+k+1]+=lazy[k];
        lazy[k+k]+=lazy[k];
        lazy[k+k+1]+=lazy[k];
        lazy[k]=0;

    }
    void add(int k,int l,int r,int l1,int r1,int x){


        if(r1==r&&l==l1){
            tre[k]+=x;
            lazy[k]+=x;
            return ;
        }
        int mid=(l+r)>>1;
        downlazy(k);
        //l->mid
        //mid+1->r
        if(l1>=mid+1){

            add(k+k+1,mid+1,r,l1,r1,x);

        }
        else if(r1<=mid){
            add(k+k,l,mid,l1,r1,x);
        }
        else{
            add(k+k,l,mid,l1,mid,x);
            add(k+k+1,mid+1,r,mid+1,r1,x);
        }
        tre[k]=max(tre[k+k],tre[k+k+1]);

    }
    int query(int k,int l,int r,int l1,int r1){
        if(r1==r&&l==l1)return tre[k];
        int mid=(r+l)>>1;
        downlazy(k);
        if(l1>=mid+1)return query(k+k+1,mid+1,r,l1,r1);
        else if(r1<=mid)return query(k+k,l,mid,l1,r1);
        else return max(query(k+k,l,mid,l1,mid),query(k+k+1,mid+1,r,mid+1,r1));

    }
    vector<bool> getResults(vector<vector<int>>& queries) {
        int len=min((int)5e4,(int)(3*queries.size()))+1;

        for(int i=0;i<=len;i++)
            add(1,0,len,i,i,i);


        vector<bool>ans;
        set<int>hasi;
        hasi.insert(len);
        hasi.insert(0);
        // 
        for(auto l:queries){
            if(l[0]==1){
                auto idx=hasi.lower_bound(l[1]);

                add(1,0,len,l[1]+1,*idx,-query(1,0,len,l[1],l[1]));
                hasi.insert(l[1]);
            }else {
                //cout<<0<<' '<<l[1]<<' '<<query(1,0,len,0,l[1])<<endl;
                if(query(1,0,len,0,l[1])>=l[2])ans.push_back(1);
                else ans.push_back(0);
            }
        }
        return ans;


    }
};
