from sortedcontainers import SortedList
class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        def cal():
            if x - slst[-1] >= l:
                return True

            for le, sl in lengthDic.items():
                if le < l:
                    continue

                idx = sl.bisect_right([x - le, x])
                if idx >= 1:
                    return True
                else:
                    q, p = sl[0]
                    y = x - l

                    if q <= y and x <= p:
                        return True

            return False

        slst = SortedList([0])
        lengthDic = defaultdict(lambda: SortedList())

        ans = []
        for q in queries:
            if q[0] == 1:
                x = q[1]
                idx = slst.bisect_right(x)
                if idx == len(slst):
                    lengthDic[x - slst[-1]].add([slst[-1], x])
                else:
                    a, b = slst[idx - 1], slst[idx]
                    lengthDic[b - a].remove([a, b])
                    if len(lengthDic[b - a]) == 0:
                        del lengthDic[b - a]

                    lengthDic[x - a].add([a, x])
                    lengthDic[b - x].add([x, b])

                slst.add(x)

            else:
                x, l = q[1], q[2]

                ans.append(cal())

        return ans
