class Solution {
public:

    vector<bool> getResults(vector<vector<int>>& queries) {
        set<int> s;
        map<int, int> mp;
        vector<bool> ans;
        for (auto q : queries) {
            if (q[0] == 1) {
                s.insert(q[1]);
                auto it = s.find(q[1]);
                if (it == s.begin()) {
                    mp[q[1]] = q[1];
                    for (auto i = next(it); i != s.end(); i = next(i)) {
                        int t = mp[*i];
                        mp[*i] = max(mp[*prev(i)], *i - *prev(i));
                        if (t == mp[*i]) break;
                    }
                } else {
                    for (auto i = it; i != s.end(); i = next(i)) {
                        int t = mp[*i];
                        mp[*i] = max(mp[*prev(i)], *i - *prev(i));
                        if (t == mp[*i]) break;
                    }
                }
            } else {
                if (s.count(q[1])) {
                    if (mp[q[1]] >= q[2]) ans.push_back(true);
                    else ans.push_back(false);
                } else {
                    s.insert(q[1]);
                    auto it = s.find(q[1]);
                    if (it == s.begin()) {
                        if (q[1] >= q[2]) ans.push_back(true);
                        else ans.push_back(false);
                    } else {
                        int ma = max(mp[*prev(it)], q[1] - *prev(it));
                        if (ma >= q[2]) ans.push_back(true);
                        else ans.push_back(false);
                    }
                    s.erase(q[1]);
                }
            }
        }
        return ans;
    }
};
