using i64 =long long ;
const int N=5e4+10;
struct node
{
    int mx;
    int flen,blen;
    int len;
    node operator+(const node &b)
    {
        node c;
        c.mx=max({mx,b.mx,blen+b.flen});
        c.flen=flen;
        c.blen=b.blen;
        if(flen==len)c.flen=flen+b.flen;
        if(b.blen==b.len)c.blen=b.blen+blen;
        c.len=len+b.len;
        return c;
    }
    void op(int x)
    {
        mx=flen=blen=x;
        len=1;
    }
};
struct
{
    int l, r;
    node c;
} tr[N << 2];
void push_up(int u)
{
    tr[u].c = tr[u << 1].c + tr[u << 1 | 1].c;
}
void build(int u, int l, int r)
{
    tr[u].l = l;
    tr[u].r = r;

    if (l == r)
    { 
        tr[u].c.op(1);
        return;
    }
    int mid = l + r >> 1;
    build(u << 1 | 1, mid + 1, r);
    build(u << 1, l, mid);
    push_up(u);
}

void modify(int u, int l, int r, int d)
{
    if (tr[u].l >= l && tr[u].r <= r)
    {
        tr[u].c.op(d);
        return;
    }
    else
    {
         int mid = tr[u].l + tr[u].r >> 1;
        if (l <= mid)
            modify(u << 1, l, r, d);
        if (r > mid)
            modify(u << 1 | 1, l, r, d);
        push_up(u);
 }
}
node ask(int u, int l, int r)
{
    if(l>r)return {};
 if (tr[u].l >= l && tr[u].r <= r)
        return tr[u].c;
    int mid = tr[u].l + tr[u].r >> 1;
    if (r <= mid)
        return ask(u << 1, l, r);
    if (l > mid)
        return ask(u << 1 | 1, l, r);
    return ask(u << 1, l, r) + ask(u << 1 | 1, l, r);
}
class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool>ans;
        int len=queries.size();
        len=min(len*3,50000);
        build(1,0,len);
        for(auto t:queries){
            int op=t[0];
            if(op==1){
                modify(1,t[1],t[1],0);
            }
            else {
                node c=ask(1,1,t[1]-1);
                if(c.mx>=t[2]-1){
                    ans.push_back(true);
                }
                else ans.push_back(false);
            }
        }
        return ans;

    }
};
