const int INF = 1e8;

class SGT
{
public:
    int n;
    vector<int> t;
    SGT(int _n)
    {
        n = _n;
        t.resize(4 * n);

    }

    int combine(int a, int b)
    {
        return max(a, b);
    }

    void update(int v, int tl, int tr, int pos, int new_val)
    {
        if (tl == tr)
        {
            t[v] = new_val;
        }
        else
        {
            int tm = (tl + tr) / 2;
            if (pos <= tm)
                update(v * 2, tl, tm, pos, new_val);
            else
                update(v * 2 + 1, tm + 1, tr, pos, new_val);
            t[v] = combine(t[v * 2], t[v * 2 + 1]);
        }
    }
    int query(int l, int r)
    {
        return query(1, 0, n - 1, l, r);
    }
    void update(int pos, int new_val)
    {
        update(1, 0, n - 1, pos, new_val);
    }
    int query(int v, int tl, int tr, int l, int r)
    {
        if (l > r)
            return -INF;
        if (l == tl && r == tr)
        {
            return t[v];
        }
        int tm = (tl + tr) / 2;
        return combine(query(v * 2, tl, tm, l, min(r, tm)), query(v * 2 + 1, tm + 1, tr, max(l, tm + 1), r));
    }
};


class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        int n = 1e5;
        SGT sgt(n + 1);
        set<int> pts;
        pts.insert(0);
        pts.insert(n);
        sgt.update(0, n);
        vector<bool> ans;
        for(int i =0;i<queries.size();i++) {
            int t = queries[i][0];
            if(t == 1) {
                int x = queries[i][1];
                auto itl = pts.lower_bound(x);
                itl--;
                int l = *itl;
                auto ith = pts.upper_bound(x);
                int r = *ith;
                sgt.update(l, x - l);
                sgt.update(x, r - x);
                pts.insert(x);
            }
            else {
                int x = queries[i][1], sz = queries[i][2];
                auto itl = pts.lower_bound(x);
                itl--;
                int l = *itl;
                int maxi = max(sgt.query(0, l - 1), x - l);
                ans.push_back(maxi >= sz);
            }
        }
        return ans;
    }
};
