class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<int> present(51000,0);
        int i,j, d = 225;
        vector<int> first(d,INT_MAX), next(d,-1), mx(d,-1);
        vector<bool> result;

        for(int k = 0; k < queries.size(); k++) {
            if(queries[k][0] == 2) {
                 bool flag = false;
                int last = 0;
                int t = queries[k][1]/ d;
                for(i=0;i<t;i++) {
                    if(mx[i] >= queries[k][2]) {
                        flag = true;
                        break;
                    }
                    if(first[i] < INT_MAX && first[i]-last >= queries[k][2]) {
                        flag = true;
                        break;
                    }
                    if(next[i] > -1)
                        last = next[i];
                    else {
                        if((i*d)-1 - last >= queries[k][2]) {
                            flag = true;
                            break;
                        }
                    }
                }
                if(!flag) {

                    for(j=i*d;j<=queries[k][1];j++) {
                        if(j - last >= queries[k][2])
                        {
                            flag = true;
                            break;
                        }
                        if(present[j])
                            last = j;
                    }
                }

                result.push_back(flag);
            } else {
               int t = queries[k][1]/ d;
                first[t] = min(first[t], queries[k][1]);
                next[t] = max(next[t], queries[k][1]);
                present[queries[k][1]] = 1;
                int last = first[t];
                mx[t] = 0;
                for(i=first[t];i<=next[t];i++) {
                    mx[t] = max(mx[t], i-last);
                    if(present[i])
                        last = i;
                }
            }

        }
        return result;
    }
};
