class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> ans;
        set<int> blocks;
        map<int,set<int>> gap;
        blocks.insert(0);
        for(auto q:queries)
        {
            if(q[0] == 1) // type 1 query
            {
                auto it = blocks.lower_bound(q[1]);
                if(it==blocks.end())
                {
                    it--;
                    gap[q[1] - (*it)].insert(*(it));
                }else{
                    auto pit = it;
                    pit--;
                    gap[*(it)-*(pit)].erase(*(pit));
                    if(gap[*(it)-*(pit)].size() == 0) 
                        gap.erase(*(it)-*(pit));
                    gap[*(it)-q[1]].insert(q[1]);
                    gap[q[1] - *(pit)].insert(*(pit));
                }
                // for(auto i:gap)
                // {
                //     cout<<i.first<<" : ";
                //     for(auto j:i.second)
                //         cout<<j<<", ";
                //     cout<<endl;
                // }
                // cout<<endl;

                blocks.insert(q[1]);
            }else{ // type 2 query
                int maxx = q[1];
                int sz = q[2];


                if(maxx-sz >= *(blocks.rbegin()))
                {
                    ans.push_back(true);
                }else{
                    auto it = gap.lower_bound(sz);
                    bool flag =false;
                    while(it!= gap.end())
                    {
                        auto iit = it->second.lower_bound(q[1]-sz);
                        if(*(iit) == (q[1]-sz) || iit != it->second.begin())
                        {
                            flag = true; break;
                        }
                        it++;
                    }
                    ans.push_back(flag);
                }
            }
        }
        return ans;
    }
};
