class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        obs =[0]
        larb = [0]
        ans = []
        for i in range(len(queries)):
            if len(queries[i]) == 2:
                x = queries[i][1]
                ind = bisect_left(obs, x)
                prev_best = larb[ind - 1]
                prev_best2 = -1
                if ind < len(obs):
                    prev_best2 = larb[ind]

                prev_diff = x - obs[ind - 1]
                next_diff = 0
                if ind < len(obs):
                    next_diff = obs[ind] - x
                obs.insert(ind, x)
                larb.insert(ind, 0)
                larb[ind] = max(prev_diff, larb[ind - 1])
                if prev_best2 != -1  and prev_best2 > prev_best:
                    for i in range(ind + 1, len(larb)):
                        larb[i] = max(larb[i - 1], obs[i] - obs[i - 1])
            else:
                q, x, sz = queries[i]
                ind = bisect_left(obs, x)
                diff = max(larb[ind - 1], x - obs[ind - 1])
                ans.append(diff >= sz)
        return ans
