class Solution {
public:

    const int maxn = 50005;

    int ST[8 * 50005];

    void update(int id, int l, int r, int i, int v) {
        if (i < l || r < i) {
            return ;
        }

        if (l == r) {
            ST[id] = v;
            return ;
        }

        int mid = (l + r) / 2;
        update(id*2, l, mid, i, v);
        update(id*2 + 1, mid+1, r, i, v);

        ST[id] = max(ST[id*2], ST[id*2 + 1]);
    }

    int get(int id, int l, int r, int u, int v) {
        if (v < l || r < u) {
            return INT_MIN;
        }

        if (u <= l && r <= v) {
            return ST[id];
        }

        int mid = (l + r) / 2;
        return max(get(id*2, l, mid, u, v), get(id*2 + 1, mid+1, r, u, v));
    }

    vector<bool> getResults(vector<vector<int>>& queries) {
        set<int> s;
        s.insert(0);
        s.insert(maxn);
        update(1, 0, maxn-1, 0, maxn);
        vector<bool> ans;
        for(auto x: queries) {
            if(x[0] == 1) {
                auto it = s.upper_bound(x[1]);
                -- it;
                auto it1 = it;
                ++ it1;
                s.insert(x[1]);
                update(1, 0, maxn-1, *it, x[1] - *it);
                update(1, 0, maxn-1, x[1], *it1 - x[1]);
            } else {
                if(x[1] < x[2]) {
                    ans.push_back(false);
                    continue;
                }
                auto r = s.upper_bound(x[1]);
                auto l = s.begin();
                -- r;
                ++ l;
                if(x[1] - *r >= x[2] || *l >= x[2]) {
                    ans.push_back(true);
                } else {
                    -- r;
                    -- l;
                    if(get(1, 0, maxn-1, *l, *r) >= x[2]) {
                        ans.push_back(true);
                    } else {
                        ans.push_back(false);
                    }
                }
            }
        }
        return ans;
    }
};
