from sortedcontainers import SortedList

class segment:
    def __init__(self,n):
        self.s = [0]*(4*n)
        self.n = n

    def _update(self,i,x,si,sl,sr):
        if sl==sr:
            self.s[si] = x
        else:
            m = (sl+sr)//2

            if i<=m:
                self._update(i,x,2*si+1,sl,m)
            else:
                self._update(i,x,2*si+2,m+1,sr)

            self.s[si] = max(self.s[2*si+1],self.s[2*si+2])

    def update(self,i,x):
        self._update(i,x,0,0,self.n-1)

    def _max(self,i,si,sl,sr):
        if i<sl:
            return 0
        elif i>=sr:
            return self.s[si]
        else:
            m = (sl+sr)//2

            return max(self._max(i,2*si+1,sl,m), self._max(i,2*si+2,m+1,sr))

    def max(self,i):
        return self._max(i,0,0,self.n-1)


class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        st = segment(50001)
        sl = SortedList()
        ans = []

        for q in queries:
            x = q[1]

            if q[0]==1:
                i = sl.bisect_left(x)
                r = sl[i] if i<len(sl) else -1
                l = sl[i-1] if i>0 else 0 
                sl.add(x)

                st.update(x,x-l)
                if r!=-1:
                    st.update(r,r-x)


                    # print('done')

#                 if x==7:
#                     for i in range(1,10):
#                         print(st.max(i),end=' ')
#                     print('')
            else:
                sz = q[2]
                i = sl.bisect_left(x)
                l = sl[i-1] if i>0 else 0
                # print(st.max(x))
                flag = (x-l>=sz) or (st.max(x)>=sz) 
                ans.append(flag)

        return ans
