set<int> use;
long long int seg_tree[200010];
void update(int l,int r,int x,int value,int id){
    if(l==r && r==x){
        seg_tree[id]=value;
        return;
    }
    if(x<l || r<x)return;
    int mid=(l+r)/2;
    update(l,mid,x,value,id*2);
    update(mid+1,r,x,value,id*2+1);
    seg_tree[id]=max(seg_tree[id*2],seg_tree[id*2+1]);
    return;
}
int query(int l,int r,int ql,int qr,int id){
    if(ql<=l && r<=qr)return seg_tree[id];
    if(r<ql || qr<l)return 0;
    int mid=(l+r)/2;
    return max(query(l,mid,ql,qr,id*2),query(mid+1,r,ql,qr,id*2+1));
}
vector<bool> answer;
class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        memset(seg_tree,0,sizeof(seg_tree));
        answer.clear();
        use.clear();
        use.insert(0);
        use.insert(50000);
        update(1,50000,50000,50000,1);
        for(int i=0;i<queries.size();i++){
            int tmp=queries[i][0];
            if(tmp==1){
                int use2=queries[i][1];
                auto it=use.lower_bound(use2);
                if(*it==use2)continue;
                update(1,50000,*it,*it-use2,1);
                it--;
                update(1,50000,use2,use2-*it,1);
                use.insert(use2);
            }else{
                int ans=0;
                auto it=use.lower_bound(queries[i][1]);
                it--;
                ans=max({ans,query(1,50000,1,*it,1),queries[i][1]-*it});
                if(ans>=queries[i][2]){
                    answer.push_back(true);
                }else{
                    answer.push_back(false);
                }
            }
        }
        return answer;
    }
};
