class Solution {


    vector<int> tree;
    vector<int> mxtl;

    int query(int l, int r, int treeId, int L, int R){
        // max number in the array mxtl from [L,R)
        // if out of bound, return 0
        // curr seg => [l,r)

        if(R<=l or L>=r)
            return 0;

        if(l>=L and r<=R)
            return tree[treeId];

        else{
            int m = (l+r)/2;
            int a = query(l,m,treeId*2+1,L,R);
            int b = query(m,r,treeId*2+2,L,R);
            return max(a,b);
        }

    }

    bool query(int x, int sz){
        int mx = query(0, 100000, 0, 0, x); // not taking the value present at x
        //cout<<mx<<" "<<sz<<endl;
        return mx >= sz;
    }

    int update(int l, int r, int treeId, int id, int val){
        if(id<l or id>=r)return tree[treeId];
        if(l==id and r==id+1){
            tree[treeId]=val;
            return val;
        }
        else{
            int m=(l+r)/2;
            int a = update(l,m,treeId*2+1,id,val);
            int b = update(m,r,treeId*2+2,id,val);
            return tree[treeId] = max(a,b);
        }
    }

    void update(int x, int val){
        //cout<<x<<" update "<<val<<endl;
        update(0,100000,0,x,val);
    }

public:
    vector<bool> getResults(vector<vector<int>>& q) {
        tree = vector<int> (1000000);
        vector<bool> ans;
        set<int> s;
        s.insert(0);
        s.insert(100000);
        update(0,100000);
        for(auto it:q){
            if(it[0]==1){
                int x = it[1];
                auto it = s.lower_bound(x);
                update(x, (*it)-x);
                it --;
                update(*it, (x-(*it)));
                s.insert(x);

            }
            else{
                int x = it[1], sz = it[2];
                auto it = s.lower_bound(x);
                it--;
                if(x-(*it) >= sz){
                    ans.push_back(true);
                }
                else
                    ans.push_back(query(*it, sz));
            }
        }
        return ans;
    }
};
