from sortedcontainers import SortedList


class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        res = []
        m = 0
        for q in queries:
            if q[0] == 1:
                m = max(m, q[1])
        v = [0] * (m + 1)
        sl = SortedList([0])
        for q in queries:
            if q[0] == 1:
                x = q[1]
                sl.add(x)
                i = sl.index(x)
                v[x] = max(v[sl[i-1]], x - sl[i-1])
                for k in range(i+1, len(sl)):
                    if v[sl[k]] == v[sl[k-1]]:
                        break
                    d = sl[k] - sl[k-1]
                    if d < v[sl[k]]:
                        v[sl[k]] = max(v[sl[k-1]], d)
                    else:
                        break
            else:
                x, sz = q[1], q[2]
                j = sl.bisect_left(x)
                if v[sl[j-1]] >= sz or x - sl[j-1] >= sz:
                    res.append(True)
                else:
                    res.append(False)
        return res
