class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& q) {
        vector<int> v(50626,0);
        vector<int> f(225,INT_MAX), l(225,-1), m(225,-1);
        int i,j, d = 225;
        vector<bool> ans;
        for(auto u : q) {
            if(u[0] == 1) {
                int t = u[1]/ d;
                f[t] = min(f[t], u[1]);
                l[t] = max(l[t], u[1]);
                v[u[1]] = 1;
                int last = f[t];
                m[t] = 0;
                for(i=f[t];i<=l[t];i++) {
                    m[t] = max(m[t], i-last);
                    if(v[i])
                        last = i;
                }
            } else {
                bool a = false;
                int last = 0;
                int t = u[1]/ d;
                for(i=0;i<t;i++) {
                    if(m[i] >= u[2]) {
                        a = true;
                        break;
                    }
                    if(f[i] < INT_MAX && f[i]-last >= u[2]) {
                        a = true;
                        break;
                    }
                    if(l[i] > -1)
                        last = l[i];
                    else {
                        if((i*d)-1 - last >= u[2]) {
                            a = true;
                            break;
                        }
                    }
                }
                if(!a) {

                    for(j=i*d;j<=u[1];j++) {
                        //cout<<j<<" "<<v[j]<<" "<<last<<endl;
                        if(j - last >= u[2])
                        {
                            a = true;
                            break;
                        }
                        if(v[j])
                            last = j;
                    }
                }

                ans.push_back(a);
            }

        }
        return ans;
    }
};
