import java.util.*;

class Solution {
    static long[] segT;

    static void update(int s, int e, int i, int idx, long val) {
        if (s == e) {
            segT[i] = val;
            return;
        }
        int mid = s + (e - s) / 2;
        if (idx <= mid) {
            update(s, mid, 2 * i + 1, idx, val);
        } else {
            update(mid + 1, e, 2 * i + 2, idx, val);
        }
        segT[i] = Math.max(segT[2 * i + 1], segT[2 * i + 2]);
    }

    static long range(int s, int e, int i, int qs, int qe) {
        if (qs <= s && qe >= e) {
            return segT[i];
        }
        if (qs > e || qe < s) {
            return Long.MIN_VALUE;
        }
        int mid = s + (e - s) / 2;
        return Math.max(range(s, mid, 2 * i + 1, qs, qe), range(mid + 1, e, 2 * i + 2, qs, qe));
    }

    public List<Boolean> getResults(int[][] queries) {
        TreeSet<Integer> set = new TreeSet<>();
        Map<Integer, Integer> map = new HashMap<>();
        List<Boolean> result = new ArrayList<>();
        int maxRange = 100000;

        set.add(0);
        set.add(maxRange);
        map.put(0, 0);
        map.put(maxRange, maxRange);
        segT = new long[5 * maxRange];

        for (int[] query : queries) {
            int type = query[0];
            int index = query[1];

            if (type == 1) {
                set.add(index);
                int prev = set.lower(index);
                int next = set.higher(index);
                map.put(index, index - prev);
                map.put(next, next - index);
                update(0, maxRange, 0, index, map.get(index));
                update(0, maxRange, 0, next, map.get(next));
            } else {
                int length = query[2];
                int prev = set.lower(index);
                if (index - prev >= length || range(0, maxRange, 0, 0, index) >= length) {
                    result.add(true);
                } else {
                    result.add(false);
                }
            }
        }

        return result;
    }
}
