class Solution 
{
public:
    vector<bool> getResults(vector<vector<int>>& queries) 
    {
        vector<bool> rst;
        map<int, int> memo;
        memo[0] = 0;
        for (int i = 0; i < queries.size(); ++i)
        {
            if (queries[i][0] == 1)
            {
                auto p = memo.lower_bound(queries[i][1]);
                if (p == memo.end())
                {
                    --p;
                    memo[queries[i][1]] = max(p->second, queries[i][1] - p->first);
                }
                else
                {
                    --p;
                    memo[queries[i][1]] = max(p->second, queries[i][1] - p->first);
                    auto q = memo.upper_bound(queries[i][1]);
                    int len = 0;
                    if(q != memo.end())
                      len = q->first - p->first;
                    for (q; q != memo.end() && q->second == len; ++q)
                    {

                        auto k = q;
                        --k;
                        q->second = max(q->first - k->first, k->second);
                    }
                }
            }
            else
            {
                int sz = queries[i][2], x = queries[i][1];
                auto p = memo.lower_bound(x);
                if (p != memo.end() && p->first == x)
                {
                    if (p->second >= sz)
                        rst.push_back(true);
                    else
                        rst.push_back(false);
                }
                else
                {
                    --p;
                    if (p->second >= sz || x - p->first >= sz)
                        rst.push_back(true);
                    else
                        rst.push_back(false);
                }
            }
        }
        return rst;
    }
};
