class Solution {
        static long[] list;

    static void check_up(int s, int e, int i, int q, long v) {
        if (s == e) {
            list[i] = v;
            return;
        }
        int mid = s + (e - s) / 2;
        if (q <= mid)
            check_up(s, mid, 2 * i + 1, q, v);
        else
            check_up(mid + 1, e, 2 * i + 2, q, v);
        list[i] = Math.max(list[2 * i + 1], list[2 * i + 2]);
    }

    static long bs(int s, int e, int i, int qs, int qe) {
        if (qs <= s && qe >= e)
            return list[i];
        if (qs > e || qe < s)
            return Long.MIN_VALUE;
        int mid = s + (e - s) / 2;
        long l = bs(s, mid, 2 * i + 1, qs, qe);
        long r = bs(mid + 1, e, 2 * i + 2, qs, qe);
        return Math.max(l, r);
    }

    static void updt_tr(TreeSet<Integer> set, HashMap<Integer, Integer> map, int position) {
        set.add(position);
        int prev = set.lower(position);
        int next = set.higher(position);
        map.put(next, next - position);
        map.put(position, position - prev);
        check_up(0, 100000, 0, position, map.get(position));
        check_up(0, 100000, 0, next, map.get(next));
    }

    static boolean checkValidity(int position, TreeSet<Integer> set, int range) {
        int prev = set.lower(position);
        return position - prev >= range || bs(0, 100000, 0, 0, position) >= range;
    }

    public List<Boolean> getResults(int[][] queries) {
         List<Boolean> ans = new ArrayList<>();
        TreeSet<Integer> set = new TreeSet<>();
        set.add(0);
        set.add(100000);
        HashMap<Integer, Integer> map = new HashMap<>();
        map.put(0, 0);
        map.put(100000, 100000);
        list = new long[5 * (int) (1e5)];
        for (int i[] : queries) {
            if (i[0] == 1) {
                updt_tr(set, map, i[1]);
            } else {
                ans.add(checkValidity(i[1], set, i[2]));
            }
        }
        return ans;
    }
}
