class SegmentTree {
private:
    vector<long> segT;
    int n;

public:
    SegmentTree(int size) {
        n = size;
        segT.resize(4 * n);
    }

    void build(int s, int e, int i, vector<long>& a) {
        if (s == e) {
            segT[i] = a[s];
            return;
        }
        int mid = s + (e - s) / 2;
        build(s, mid, 2 * i + 1, a);
        build(mid + 1, e, 2 * i + 2, a);
        segT[i] = max(segT[2 * i + 1], segT[2 * i + 2]);
    }

    void update(int s, int e, int i, int q, long v) {
        if (s == e) {
            segT[i] = v;
            return;
        }
        int mid = s + (e - s) / 2;
        if (q <= mid)
            update(s, mid, 2 * i + 1, q, v);
        else
            update(mid + 1, e, 2 * i + 2, q, v);
        segT[i] = max(segT[2 * i + 1], segT[2 * i + 2]);
    }

    long range(int s, int e, int i, int qs, int qe) {
        if (qs <= s && qe >= e)
            return segT[i];
        if (qs > e || qe < s)
            return INT_MIN;
        int mid = s + (e - s) / 2;
        long l = range(s, mid, 2 * i + 1, qs, qe);
        long r = range(mid + 1, e, 2 * i + 2, qs, qe);
        return max(l, r);
    }
};

class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> arr;
        set<int> s;
        map<int, int> m;
        s.insert(0);
        s.insert(100000);
        m[0] = 0;
        m[100000] = 100000;
        SegmentTree st(100001);

        for (const auto& i : queries) {
            if (i[0] == 1) {
                s.insert(i[1]);
                int prev = *(--s.lower_bound(i[1]));
                int next = *s.upper_bound(i[1]);
                m[next] = next - i[1];
                m[i[1]] = i[1] - prev;
                st.update(0, 100000, 0, i[1], m[i[1]]);
                st.update(0, 100000, 0, next, m[next]);
            } else {
                int prev = *(--s.lower_bound(i[1]));
                if (i[1] - prev >= i[2]) {
                    arr.push_back(true);
                    continue;
                } else if (st.range(0, 100000, 0, 0, i[1]) >= i[2]) {
                    arr.push_back(true);
                    continue;
                }
                arr.push_back(false);
            }
        }

        return arr;
    }
};
