class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        set<int> obs;
        set<int> us;
        unordered_map<int,int> mp;
        unordered_map<int,set<int>> ms;
        vector<bool> ans;
        obs.insert(0);
        for(auto query:queries)
        {
            if(query[0]==1)
            {
                int x = query[1];
                if(obs.count(x))
                    continue;
                if(obs.size()==1)
                {
                    mp[x]++;
                    obs.insert(x);
                    us.insert(x);
                    ms[x].insert(0);
                }
                else
                {
                    auto iter = obs.lower_bound(x);
                    if(iter==obs.end())
                    {
                        int y = *obs.rbegin();
                        mp[x-y]++;
                        obs.insert(x);
                        us.insert(x-y);
                        ms[x-y].insert(y);
                        continue;
                    }
                    int r = *iter;
                    iter--;
                    int l = *iter;
                    // cout << r <<' ' <<x<<' '<< l << endl;
                    mp[r-l]--;
                    ms[r-l].erase(l);
                    if(mp[r-l]==0)
                    {
                        us.erase(r-l);
                    }
                    mp[r-x]++;
                    mp[x-l]++;
                    us.insert(r-x);
                    us.insert(x-l);
                    ms[r-x].insert(x);
                    ms[x-l].insert(l);
                    obs.insert(x);
                }
            }
            else if(query[0]==2)
            {
                int x = query[1];
                int sz = query[2];
                // cout << x << ' ' << sz << endl;
                auto iter1 = obs.lower_bound(x);
                iter1--;
                int y = *iter1;
                // cout << y << endl;
                if(x-y>=sz)
                {
                    ans.push_back(true);
                    continue;
                }

                // for(auto uss:us)
                //     cout << uss << ' ';
                // cout << endl;

                auto iter = us.lower_bound(sz);
                if(iter==us.end())
                {
                    ans.push_back(false);
                    continue;
                }
                else
                {
                    // cout << *iter << endl;
                    while(iter!=us.end()&&*ms[*iter].begin()>x-sz)
                    {
                        iter++;
                    }
                    if(iter!=us.end())
                    {
                        ans.push_back(true);
                    }
                    else
                        ans.push_back(false);
                    continue;
                }

            }
        }
        return ans;
    }
};
