class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> ans;
        vector<int> lin(5*1e5,0);
        for(auto it: queries){
            if(it.size()==2){
                lin[it[1]] = 1;
            }
            else{
                int t=0;
                int x = it[1];
                int sz = it[2];
                if(x<sz){
                    ans.push_back(false);
                    continue;
                }
                for(int i=0;i<x-sz+1;i++){
                    t=0;
                    for(int u=i+sz-1;u>i;u--){
                        if(lin[u]){
                            t=1;
                            i = u-1;
                            break;
                        }
                    }
                    if(!t){
                        ans.push_back(true);
                        break;
                    }
                }
                if(t){
                    ans.push_back(false);
                }
            }
        }
        return ans;
    }
};
