class Solution {
public:
    int t[300009];
    int sum(int v, int tl, int tr, int l, int r) {
    if (l > r) 
        return 0;
    if (l == tl && r == tr) {
        return t[v];
    }
    int tm = (tl + tr) / 2;
    return max(sum(v*2, tl, tm, l, min(r, tm))
           , sum(v*2+1, tm+1, tr, max(l, tm+1), r));
}
    void update(int v, int tl, int tr, int pos, int new_val) {
    if (tl == tr) {
        t[v] = new_val;
    } else {
        int tm = (tl + tr) / 2;
        if (pos <= tm)
            update(v*2, tl, tm, pos, new_val);
        else
            update(v*2+1, tm+1, tr, pos, new_val);
        t[v] = max(t[v*2] , t[v*2+1]);
    }
}
    vector<bool> getResults(vector<vector<int>>& q) {
        vector<bool> ans;
        set<int> s;
        int mx=0;
        for(auto x:q)
        {
            if(x[0]==1)
            {
                mx=max(mx,x[1]);
            }
            else
                mx=max({mx,x[1],x[2]});
        }
        mx+=10;
        s.insert(0);
        s.insert(mx);
        int i=0;
        while(i<=mx)
        {
            t[i]=0;
            i++;
        }
        update(1,0,mx,0,mx);
        for(auto x:q)
        {
            if(x[0]==1)
            {
                auto it=s.lower_bound(x[1]);
                auto ti=it;
                ti--;
                // cout<<*it<<" "<<*ti<<"\n";
                update(1,0,mx,*ti,x[1]-*ti);
                s.insert(x[1]);
                update(1,0,mx,x[1],*it-x[1]);
            }
            else
            {
                auto it=s.lower_bound(x[1]);
                it--;
                if(*it+x[2]<=x[1])
                    ans.push_back(true);
                else
                {
                    // cout<<*it<<" ";
                    int g=sum(1,0,mx,0,*it-1);
                    // cout<<g;
                    ans.push_back(g>=x[2]);
                }
            }
        }
        return ans;
    }
};
