class Solution {
    public List<Boolean> getResults(int[][] queries) {
        List<Boolean> res = new ArrayList<>();
        TreeMap<Integer, Integer> m = new TreeMap<>();
        m.put(0, 0);
        for(int[] q : queries){
            int val = q[1];
            if(q[0] == 1){
                Map.Entry<Integer, Integer> e = m.lowerEntry(val);
                m.put(val, Math.max(e.getValue(), val - e.getKey()));
                Integer h = m.higherKey(val);
                int curr = val;
                int cm = m.get(val);
                while(h != null){
                    cm = Math.max(cm, h - curr);
                    if(m.get(h) == cm){
                        break;
                    }
                    m.put(h, cm);
                    curr = h;
                    h = m.higherKey(h);
                }
            }else{
                if(m.containsKey(val)){
                    if(m.get(val) >= q[2]){
                        res.add(true);
                    }else{
                        res.add(false);
                    }
                }else{
                    Map.Entry<Integer, Integer> e = m.lowerEntry(val);
                    int cm = Math.max(e.getValue(), val - e.getKey());
                    if(cm >= q[2]){
                        res.add(true);
                    }else{
                        res.add(false);
                    }
                }
            }
        }
        return res;
    }
}
