class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) 
    {
        map<int,int> data;
        data[0] = 0;
        vector<bool> ans;
        for(auto x:queries)
        {
            auto iter = data.upper_bound(x[1]);
            iter--;
            int prev = iter->first;
            iter++;
            // while(iter!=data.end() and iter->first<x[1])
            // {
            //     prev = iter->first;
            //     iter++;
            // }
            if(x.size() == 2)
            {
                data[x[1]] = max(data[prev],x[1] - prev);
                prev = x[1];
                while(iter!=data.end())
                {
                    if( iter->second <= data[prev])
                    {
                        break;
                    }
                    iter->second = max(data[prev],iter->first - prev);
                    prev = iter->first;
                    iter++;
                }
            }
            else
            {
                if((data[prev] >= x[2]) or (x[1] - prev >= x[2])) ans.push_back(true);
                else ans.push_back(false);
            }
            // for(auto x:data)
            // {
            //     cout << x.first << " " << x.second << endl;
            // }
            // cout << endl;
        }
        return ans;
    }
};
