class Solution {
public:
    vector<bool> getResults(vector<vector<int>>& queries) {
        vector<bool> ans;
        set<int> st;
        map<int, set<int>> mp;

        for (auto q : queries) {
            if (q[0] == 1) {
                int x = q[1];
                auto lb = st.lower_bound(x);
                int next = -1, prev = 0;
                if (lb != st.end()) {
                    next = *lb;
                }
                if (lb != st.begin()) {
                    lb--;
                    prev = *lb;
                }

                // remove diff of next
                if (next != -1) {
                    int d = next - prev;
                    mp[d].erase(next);
                    if(mp[d].size() == 0){
                        mp.erase(d);
                    }
                }

                // add x
                st.insert(x);
                int dp = x - prev;
                mp[dp].insert(x);

                if (next != -1) {
                    int dn = next - x;
                    mp[dn].insert(next);
                }
            } else {
                int x = q[1], sz = q[2];
                if (st.empty()) {
                    ans.push_back(sz <= x);
                } else {
                    int maxDiff = -1;
                    auto it = mp.end();
                    it--;
                    for (; it != mp.begin(); --it) {

                        if (*((it->second).begin()) <= x) {
                            maxDiff = it->first;
                            break;
                        }
                    }

                    if (*((mp.begin()->second).begin()) <= x) {
                        maxDiff = it->first;
                    }

                    auto lb = st.lower_bound(x);
                    int prev=0;
                    if (lb != st.begin()) {
                        lb--;
                        prev = *lb;
                    }

                    int diff = x - prev;
                    if (diff >= sz || maxDiff >= sz)
                        ans.push_back(true);
                    else
                        ans.push_back(0);
                }
            }
        }

        return ans;
    }
};
