class Solution:
    def getResults(self, queries: List[List[int]]) -> List[bool]:
        b = [1e7]
        cnt = {1e7: 1e7}

        ans = []
        for q in queries:
            if q[0] == 1:
                i = bisect.bisect_left(b, q[1])
                bisect.insort_left(b, q[1])
                if i == 0:
                    cnt[b[i]] = b[i]
                    cnt[b[i + 1]] = max(b[i + 1] - b[i], cnt[b[i]])
                else:
                    cnt[b[i]] = max(cnt[b[i - 1]], q[1] - b[i - 1])
                    cnt[b[i + 1]] = max(b[i + 1] - b[i], cnt[b[i]])
                for j in range(i + 2, len(b)):
                    if cnt[b[j]] <= cnt[b[j - 1]]:
                        break
                    cnt[b[j]] = max(b[j] - b[j - 1], cnt[b[j - 1]])

            else:
                _, x, z = q
                i = bisect.bisect_left(b, x)
                if i == 0:
                    ans.append(z <= x)
                elif b[i] == x:
                    ans.append(z <= cnt[b[i]])
                else:
                    ans.append(z <= max(x - b[i - 1], cnt[b[i - 1]]))
                # if z <= max(b[i + 1] - )
            # print(b, cnt)
        return ans
